 * getTranslationKey() æŸ¥çœ‹å½“å‰æ–°å¢žkeyï¼Œä»…åœ¨run devçŠ¶æ€ä¸‹å¯ç”¨
 * uploadTranslationKey(èµ„æºåœ°å€ï¼Œç¿»è¯‘å¹³å°é‰´æƒtoken)
 */

function getTranslationKey() {
  let keys = sessionStorage.getItem('collectKeys');
  if (keys) {
    keys = JSON.parse(keys);
  } else {
    keys = {};
  }
  return keys;
}

function clearTranslationKey() {
  sessionStorage.removeItem('collectKeys');
  console.log('Removed!');
}

function stringToArrayBuffer(str) {
  const buffer = new ArrayBuffer(str.length);
  const view = new Uint8Array(buffer);
  for (let i = 0; i < str.length; ++i) {
    view[i] = str.charCodeAt(i) & 0xff;
  }
  return buffer;
}

const postKeys = async ({ keys, resourceURL, token }) => {
  const check = window.prompt('These key will be upload, please enter y to confirm:\n' + Object.keys(keys).join('\n'));
  if (check !== 'y') return;
  const uri = resourceURL.replace('resources', 'api/resources').replace('lang', 'languagestoken') + '/import/json';
  const data = JSON.stringify(keys, null, 4);
  const form = new FormData();
  const blob = new Blob([stringToArrayBuffer(data)], { type: 'application/json' });
  form.append('file', blob, 'dev.json');

  const response = await fetch(uri, {
    body: form,
    headers: {
      Authorization: token
    },
    method: 'POST'
  });
  const result = await response.json();
  return result;
};

async function uploadTranslationKey(resourceURL, token) {
  resourceURL = resourceURL || localStorage.getItem('gt_resourceURL');
  token = token || localStorage.getItem('gt_token');
  if (!resourceURL || !token) {
    console.error('resourceURL and token is required');
    return;
  }
  localStorage.setItem('gt_resourceURL', resourceURL);
  localStorage.setItem('gt_token', token);
  console.log('resourceURL and token is save in localStorage');

  const keys = getTranslationKey();
  const res = await postKeys({ keys, resourceURL, token });
  if (res && res.status === 'success') {
    console.log('Upload Success!', { request: res.requests.updated, ...res.entities });
    clearTranslationKey();
    return 'Uploading....';
  }

  console.error(res, 'Upload Failed!');
  return;
}

function addTranslationKey(key, value) {
  const keys = getTranslationKey();

  if (keys[key] && keys[key] !== value) {
    console.error(`the new key [${key}] is conflict: [old]${keys[key]} [new]${value}`);
    return;
  }

  keys[key] = value;

  sessionStorage.setItem('collectKeys', JSON.stringify(keys));
  window.getTranslationKey = getTranslationKey;
  window.clearTranslationKey = clearTranslationKey;
  window.uploadTranslationKey = uploadTranslationKey;
}

// é»˜è®¤ä¸ºcommon
export function gt(label, namespace) {
  const key = (namespace || 'common') + (label || '').replace(/\s/g, '');

  if (process.env.NODE_ENV === 'development') {
    if (!label) {
      throw new Error('label is required!');
    }
    // if (!namespace) {
    //   throw new Error(`namespace is required for ${label}`);
    // }

    // æ£€æµ‹æ–°å¢ž
    const hasKey = i18n.te(key);
    if (!hasKey) {
      addTranslationKey(key, label);
    } else {
      // æ£€æµ‹å†²çª
      if (i18n.t(key) !== label) {
        console.error(`you key [${key}] is already existed but different: [old]${i18n.t(key)} [new]${label}`);
      }
    }
  }

  return label ? i18n.t(key) : '';
}

Vue.prototype.$gt = gt;

