<template>
  <div>
    <layout-detail
    >
    <template slot="auto-title">
      <i class="el-icon-arrow-left go-back-icon" @click="goBack"></i>
      <span class="detail-header-title">{{ requestId }}</span>
      <span :class="`pms-status-sign pms-status-sign-${statusInfo.color}`">{{statusInfo.text}}</span>
      <p v-if="isPendingApprove" class="sub-title">{{`${`${$t('IRRemainIBQuota')}: ${remainIBQty || '/'}`}`}}</p>
    </template>
      <div slot="header-right" class="actions">
        <el-button
          class="white-blue"
          @click="onExportClick"
          :loading="isExporting"
        >
          {{$t('commonExport')}}
        </el-button>
        <el-button class="white-blue" @click="onActionLogClick">
          {{$t('commonActionLog')}}
        </el-button>
        <el-button v-show="detail.can_modify" class="white-blue" @click="onModifyClick">
          {{$t('commonModify')}}
        </el-button>
        <el-button v-show="detail.can_cancel" class="white-red" @click="onCancelClick">
          {{$t('commonCancelRequest')}}
        </el-button>
        <el-button
          v-show="detail.can_approve"
          class="white-green"
          @click="onApproveClick"
          :loading="isApproving"
        >
          {{$t('commonApproveAndNext')}}({{detail.pending_num}})
        </el-button>
        <el-button v-show="detail.can_approve" class="white-red" @click="onRejectClick">
          {{$t('commonRejectAndNext')}}({{detail.pending_num}})
        </el-button>
        <el-button v-show="detail.can_create_pr" class="white-blue" @click="onCreatePrClick">
          {{$t('commonCreatePr')}}
        </el-button>
        <el-button v-show="detail.can_release" class="white-blue" @click="onReleaseClick">
          {{$t('commonRelease')}}
        </el-button>
      </div>
      <div slot="content">
        <div v-for="item in items" :key= "item.label" class="item">
          <div class="label">{{item.label}} :</div>
          <div>{{item.value}}</div>
        </div>
      </div>
    </layout-detail>
    <action-log
        type="action"
        :visible="isShowActionLogs"
        @close="isShowActionLogs = false"
        :params="actionLogParams"
    ></action-log>
  </div>
</template>

<script>
import moment from 'moment';
import LayoutDetail from '@/components/layout/detail';
import ActionLog from '@/components/actionLogDialog/actionLog';
import {
  getInboundReservationDetail,
  postExportInboundReservation,
  postApproveInboundReservation,
  postCancelInboundReservation,
  postReleaseInboundReservation,
  postCheckInboundReservationRequestCancel,
  postCheckInboundReservationRequestRelease
} from '@/api/inboundReservation';
import {downloadBlobFile} from '@/utils/downloadFile';
import checkIRRemainQty from '@/mixins/checkIRRemainQty';
const DATE_FORMAT = 'YYYYMMDD';

const STATUS_NUMBER_TO_DETAIL = {
  1: {key: 'commonStatusPendingApproval', color: 'blue'},
  2: {key: 'commonStatusApproved', color: 'green'},
  3: {key: 'commonStatusRejected', color: 'red'},
  4: {key: 'commonStatusCancelled', color: 'grey'},
  5: {key: 'commonStatusReleased', color: 'grey'}
};

export default {
  mixins: [checkIRRemainQty],
  components: {
    'layout-detail': LayoutDetail,
    'action-log': ActionLog
  },
  data() {
    return {
      detail: {},
      isExporting: false,
      isApproving: false,
      isShowActionLogs: false
    };
  },
  mounted() {
    this.fetchDetail();
  },
  methods: {
    goBack() {
      this.$router.go(-1);
    },
    async fetchDetail() {
      try {
        const {data = {}, retcode, info} = await getInboundReservationDetail({request_id: this.requestId});
        if (retcode === 0) {
          this.detail = data;
          this.getWmsRemainingIbQuota(this.detail.whs_id, this.detail.inbound_date_str, this.detail.vendor_site_id);
        } else {
          this.$message({ type: 'error', message: info });
        }
      } catch (e) {
        this.$message({ type: 'error', message: e });
      }
    },
    async onExportClick() {
      try {
        this.isExporting = true;
        const result = await postExportInboundReservation({request_id: this.requestId});
        const date = moment().format(DATE_FORMAT);
        const fileName = `${this.requestId}${date}.xls`;
        downloadBlobFile(result, fileName);
      } catch (e) {
        this.$message({ type: 'error', message: e });
      } finally {
        this.isExporting = false;
      }
    },
    onModifyClick() {
      this.$router.push(`/admin/inbound/IBR/create?modifyId=${this.requestId}`);
    },
    async onApproveClick() {
      try {
        this.isApproving = true;
        const params = {
          request_id: this.requestId,
          action: 2,
          remark: ''
        };
        const {retcode, info, data} = await postApproveInboundReservation(params);
        if (retcode !== 0) {
          this.$message.error(info);
        } else {
          if (data.next_request_id) {
            this.$router.push(`/admin/inbound/IBR/detail/${data.next_request_id}`);
          }
          this.fetchDetail();
        }
      } catch (e) {
        this.$message({ type: 'error', message: e });
      } finally {
        this.isApproving = false;
      }
    },
    rejectReasonValidator(value) {
      if (!value) {
        return this.$t('commonIsRequired', {target: this.$t('commonRejectReason')});
      }
      if (value.length > 500) {
        return this.$t('commonLimitChars', {num: 500});
      }
      return true;
    },
    onRejectClick() {
      this.$prompt(this.$t('commonRejectReason'), this.$t('commonReject'), {
        confirmButtonText: this.$t('commonConfirm'),
        cancelButtonText: this.$t('commonCancel'),
        inputValidator: this.rejectReasonValidator
      }).then(({value}) => {
        this.onRejectConfirm(value);
      });
    },
    async onRejectConfirm(reason) {
      try {
        const params = {
          request_id: this.requestId,
          action: 3,
          remark: reason || ''
        };
        const {retcode, info, data} = await postApproveInboundReservation(params);
        if (retcode !== 0) {
          this.$message.error(info);
        } else {
          if (data.next_request_id) {
            this.$router.push(`/admin/inbound/IBR/detail/${data.next_request_id}`);
          }
          this.fetchDetail();
        }
      } catch (e) {
        this.$message({ type: 'error', message: e });
      }
    },
    async onCancelClick() {
      try {
        const {data} = await postCheckInboundReservationRequestCancel({request_ids: [this.requestId]});
        if (!data.is_ok) {
          this.$message.error(data.err_msg);
          return;
        }
        this.$confirm(
          this.$t('IRAreSureToCancel'),
          this.$t('commonCancel'),
          {
            confirmButtonText: this.$t('commonConfirm'),
            cancelButtonText: this.$t('commonNotNow')
          }
        ).then(() => {
          this.onCancelConfirm();
        });
      } catch (e) {
        this.$message({ type: 'error', message: e });
      }
    },
    async onCancelConfirm() {
      try {
        const {retcode, info} = await postCancelInboundReservation({request_ids: [this.requestId]});
        if (retcode !== 0) {
          this.$message.error(info);
        } else {
          this.$message.success(this.$t('commonCancelSuccessfully'));
        }
        this.fetchDetail();
      } catch (e) {
        this.$message({ type: 'error', message: e });
      }
    },
    async onReleaseClick() {
      try {
        const {data} = await postCheckInboundReservationRequestRelease({request_ids: [this.requestId]});
        if (!data.is_ok) {
          this.$message.error(data.err_msg);
          return;
        }
        this.$confirm(
          this.$t('IRAreSureToRelease'),
          this.$t('commonRelease'),
          {
            confirmButtonText: this.$t('commonConfirm'),
            cancelButtonText: this.$t('commonNotNow')
          }
        ).then(() => {
          this.onReleaseConfirm();
        });
      } catch (e) {
        this.$message({ type: 'error', message: e });
      }
    },
    async onReleaseConfirm() {
      try {
        const {retcode, info} = await postReleaseInboundReservation({request_ids: [this.requestId]});
        if (retcode !== 0) {
          this.$message.error(info);
        } else {
          this.$message.success(this.$t('commonReleaseSuccessfully'));
        }
        this.fetchDetail();
      } catch (e) {
        this.$message({ type: 'error', message: e });
      }
    },
    onCreatePrClick() {
      const {whs_id, sbs_vendor_id, inbound_date_str, vendor_site_id } = this.detail;
      const data = {
        ibr_id: this.requestId,
        warehouse: whs_id,
        sbs_vendor_id: sbs_vendor_id,
        expected_inbound_date: inbound_date_str,
        vendor_site_id
      };
      this.$store.commit('SET_IBR_CREATE_PR_DATA', data);
      this.$router.push('/admin/purchase/request/create');
    },
    onActionLogClick() {
      this.isShowActionLogs = true;
    }
  },
  computed: {
    requestId() {
      return this.$route.params.id || '';
    },
    isPendingApprove() {
      return this.detail.request_status === 1;
    },
    actionLogParams() {
      return {
        log_type: 'inbound_reservations_request',
        biz_type: 'INBOUND',
        object_id: this.requestId,
        show_type: 1
      };
    },
    items() {
      const {detail} = this;
      const {shop_ids = [], shop_names = []} = detail;
      const shops = shop_ids.map((id, index) => `${id}/${shop_names[index]}`).join('ã€');
      return [
        {label: this.$t('commonWarehouseID'), value: detail['whs_id']},
        {label: this.$t('commonInboundDate'), value: detail['inbound_date_str']},
        {label: this.$t('supplierSBSVendorID'), value: detail['sbs_vendor_id']},
        {label: this.$t('commonVendorSiteID'), value: detail['vendor_site_id']},
        {label: this.$t('commonSupplierName'), value: detail['supplier_name']},
        {label: this.$t('IRReservedQty'), value: detail['reserved_qty']},
        {label: this.$t('IRPMORKAM'), value: detail['pm_kam_email']},
        {label: this.$t('commonReason'), value: detail['reason']},
        {label: this.$t('budgetCategoryCluster'), value: (detail['category_cluster'] || []).join('ã€')},
        {label: this.$t('IBRExpectedNumberOfSkus'), value: detail['sku_amount']},
        {label: this.$t('commonShopIDOrName'), value: shops},
        {label: this.$t('commonComment'), value: detail['comment']},
        {label: this.$t('IBRReservationUtilization'), value: detail['utilization'] }
      ];
    },
    statusInfo() {
      const status = STATUS_NUMBER_TO_DETAIL[this.detail.request_status] || {};
      return {
        color: status.color || 'blue',
        text: status.key ? this.$t(status.key) : ''
      };
    }
  }
};
</script>

